import containerStyle from "./EditModal.module.css";
import WrapperModal from "../wrapper/WrapperModal";
import { useContext, useState } from "react";
import TaskContext from "../../store/task-list";

const EditModal = () => {
  const { modalEditView, checkModalEditView, editedTask } = useContext(TaskContext);
  const [textTaskInput, getTextTaskInput] = useState(modalEditView.name);



  const setTaskInput = (event) => {
    getTextTaskInput(event.target.value);
  };


  const postEditFormTask = (event) => {
    event.preventDefault()
    editedTask({
        id: modalEditView.id,
        name: textTaskInput,
        priority: modalEditView.priority
    })

  }


  return (
    <WrapperModal>
      <form on>
        <div className={containerStyle.addEditModal}>
          <div className={containerStyle.flxBetween}>
            <span className={containerStyle.modalTitle}> Edit Task </span>
            <svg
              width="17"
              height="16"
              viewBox="0 0 17 16"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
              className={containerStyle.cp}
              onClick={() =>
                checkModalEditView((prevState) => ({
                  ...prevState,
                  statusModal: false,
                }))
              }
            >
              <path
                fillRule="evenodd"
                clipRule="evenodd"
                d="M16.0456 0.716817C15.603 0.302896 14.9103 0.302151 14.4668 0.715119L8.49994 6.27064L2.53309 0.715122C2.08954 0.302153 1.39691 0.302898 0.954273 0.716819L0.898833 0.768663C0.41891 1.21745 0.419741 1.97264 0.900651 2.4204L6.76795 7.88323L0.900653 13.3461C0.419743 13.7938 0.418912 14.549 0.898835 14.9978L0.954275 15.0496C1.39691 15.4636 2.08955 15.4643 2.53309 15.0513L8.49994 9.49582L14.4668 15.0513C14.9103 15.4643 15.603 15.4636 16.0456 15.0496L16.101 14.9978C16.581 14.549 16.5801 13.7938 16.0992 13.3461L10.2319 7.88323L16.0992 2.4204C16.5801 1.97264 16.581 1.21745 16.101 0.768661L16.0456 0.716817Z"
                fill="#0A1629"
              ></path>
            </svg>
          </div>
          <div className={containerStyle.input}>
            <label htmlFor="title">Task</label>
            <input
              type="text"
              placeholder="Type your task here..."
              name="title"
              value={textTaskInput}
              onChange={setTaskInput}
            ></input>
          </div>
          <div className={containerStyle.modalPriority}>
            <span>Priority</span>
            <ul className={containerStyle.priorityButtons}>
              <li
                className={`${
                  modalEditView.priority === 3 && containerStyle.highSelected
                } ${containerStyle.high}`}
                onClick={() =>
                  checkModalEditView((prevState) => ({
                    ...prevState,
                    priority: 3,
                  }))
                }
              >
                high
              </li>
              <li
                className={`${
                  modalEditView.priority === 2 && containerStyle.mediumSelected
                } ${containerStyle.medium}`}
                onClick={() =>
                  checkModalEditView((prevState) => ({
                    ...prevState,
                    priority: 2,
                  }))
                }
              >
                medium
              </li>
              <li
                className={`${
                  modalEditView.priority === 1 && containerStyle.lowSelected
                } ${containerStyle.low}`}
                onClick={() =>
                  checkModalEditView((prevState) => ({
                    ...prevState,
                    priority: 1,
                  }))
                }
              >
                low
              </li>
            </ul>
          </div>
          <div className={containerStyle.flxRight}>
            <button
              className={containerStyle.button}
              disabled={textTaskInput.trim().length <= 0 ? true : false}
            >
              Edit
            </button>
          </div>
        </div>
      </form>
    </WrapperModal>
  );
};

export default EditModal;
