import React, { useCallback, useEffect } from "react";
import DEFAULT_TASKS from "./ConstantsVariable";


const TaskContext = React.createContext();

export const TaskContextProvider = ({ children }) => {
    const addStructure = useCallback((arr) => {
        return arr
          .map((obj) => {
            if (obj.priority === 1) {
              return {
                ...obj,
                priorityText: "Low",
                priorityColor: "colorLow",
              };
            } else if (obj.priority === 2) {
              return {
                ...obj,
                priorityText: "Medium",
                priorityColor: "colorMedium",
              };
            } else if (obj.priority === 3) {
              return {
                ...obj,
                priorityText: "High",
                priorityColor: "colorHigh",
              };
            }
          })
          .map((obj) => {
            if (obj.status === 1) {
              return {
                ...obj,
                statusText: "Done",
                statusProgress: 69.115,
              };
            } else if (obj.status === 2) {
              return {
                ...obj,
                statusText: "In process",
                statusProgress: 34.5575,
              };
            } else if (obj.status === 3) {
              return {
                ...obj,
                statusText: "To do",
                statusProgress: 0,
              };
            }
          });
      });
    useEffect(()=>addStructure())  
    const changeStatus = (id) => {
    DEFAULT_TASKS.map((obj) => {
      if (obj.id === id) {
        console.log(obj);

        if (obj.status <= 2) {
          return (obj.status += 1);
        } else {
          return (obj.status = 1);
        }
      }
    });
  };

  
  return (
    <TaskContext.Provider
      value={{
        data: addStructure(DEFAULT_TASKS),
        changeStatus: changeStatus,
      }}
    >
      {children}
    </TaskContext.Provider>
  );
};

export default TaskContext;
