import WrapperModal from "../UI/wrapper/WrapperModal";
import { useContext, useState } from "react";
import TaskContext from "../../store/task-list";
import FormModal from "../UI/formModal/FormModal";

const EditModal = () => {
  const { modalEditView, checkModalEditView, editedTask } =
    useContext(TaskContext);
  const [textTaskInput, getTextTaskInput] = useState(modalEditView.name);

  const setTaskInput = (event) => {
    getTextTaskInput(event.target.value);
  };

  const postEditFormTask = (event) => {
    event.preventDefault();
    editedTask({
      id: modalEditView.id,
      name: textTaskInput,
      priority: modalEditView.priority,
    });
    checkModalEditView((prevState) => ({
      ...prevState,
      statusModal: false,
    }));
  };

  close

  const getStatusHigh = () => {
    checkModalEditView((prevState) => ({
      ...prevState,
      priority: 3,
    }));
  };
  const getStatusMedia = () => {
    checkModalEditView((prevState) => ({
      ...prevState,
      priority: 2,
    }));
  };
  const getStatusLow = () => {
    checkModalEditView((prevState) => ({
      ...prevState,
      priority: 1,
    }));
  };

  return (
    <WrapperModal>
      <FormModal
        modalEditView={modalEditView}
        checkModalEditView={checkModalEditView}
        textTaskInput={textTaskInput}
        setTaskInput={setTaskInput}
        postEditFormTask={postEditFormTask}
        getStatusHigh={getStatusHigh}
        getStatusMedia={getStatusMedia}
        getStatusLow={getStatusLow}
        textButton={"Edit"}
      />
    </WrapperModal>
  );
};

export default EditModal;
