import { useEffect } from "react";
import DEFAULT_TASKS from "../store/ConstantsVariable";



const useStructure = (arr) => {
    useEffect(()=>{console.log(arr,'arr')}, [arr])
    return arr.map((obj) => {
      if (obj.priority === 1) {
        return {
          ...obj,
          priorityText: "Low",
          priorityColor: "colorLow",
        };
      } else if (obj.priority === 2) {
        return {
          ...obj,
          priorityText: "Medium",
          priorityColor: "colorMedium",
        };
      } else if (obj.priority === 3) {
        return {
          ...obj,
          priorityText: "High",
          priorityColor: "colorHigh",
        };
      }
    }).map((obj) => {
      if (obj.status === 1) {
        return {
          ...obj,
          statusText: "Done",
          statusProgress: 69.115,
        };
      } else if (obj.status === 2) {
        return {
          ...obj,
          statusText: "In process",
          statusProgress: 34.5575,
        };
      } else if (obj.status === 3) {
        return {
          ...obj,
          statusText: "To do",
          statusProgress: 0,
        };
      }
    });

  }

  export default useStructure;
