import React from "react";
import DEFAULT_TASKS from "./ConstantsVariable";

const TaskContext = React.createContext(DEFAULT_TASKS);
const filter = (arr) => {
  let arr1 = arr.map((obj) => {
    if (obj.priority === 1) {
      return {
        ...obj,
        priorityText: "Low",
        priorityColor: "#0ac947",
      };
    } else if (obj.priority === 2) {
      return {
        ...obj,
        priorityText: "Medium",
        priorityColor: "#ffbd21",
      };
    } else if (obj.priority === 3) {
      return {
        ...obj,
        priorityText: "High",
        priorityColor: "#f73446",
      };
    }
    if (obj.status === 1) {
      return {
        ...obj,
        statusText: "Done",
        statusProgress: 69.115,
      };
    } else if (obj.status === 2) {
      return {
        ...obj,
        statusText: "In process",
        statusProgress: 34.5575,
      };
    } else if (obj.status === 3) {
      return {
        ...obj,
        statusText: "To do",
        statusProgress: 0,
      };
    }
  });

  let arr2 = arr1.forE((obj) => {
    if (obj.status === 1) {
      return {
        ...obj,
        statusText: "Done",
        statusProgress: 69.115,
      };
    } else if (obj.status === 2) {
      return {
        ...obj,
        statusText: "In process",
        statusProgress: 34.5575,
      };
    } else if (obj.status === 3) {
      return {
        ...obj,
        statusText: "To do",
        statusProgress: 0,
      };
    }
  });

  return arr2;
};

console.log(filter(DEFAULT_TASKS), "useFilter");

export const TaskContextProvider = ({ children }) => {
  return (
    <TaskContext.Provider value={DEFAULT_TASKS}>
      {children}
    </TaskContext.Provider>
  );
};

export default TaskContext;
