import React, { useState } from "react";
import DEFAULT_TASKS from "./ConstantsVariable";

const TaskContext = React.createContext();
const addStructure = (arr) => {
  return arr
    .map((obj) => {
      if (obj.priority === 1) {
        return {
          ...obj,
          priorityText: "Low",
          priorityColor: "colorLow",
        };
      } else if (obj.priority === 2) {
        return {
          ...obj,
          priorityText: "Medium",
          priorityColor: "colorMedium",
        };
      } else if (obj.priority === 3) {
        return {
          ...obj,
          priorityText: "High",
          priorityColor: "colorHigh",
        };
      }
    })
    .map((obj) => {
      if (obj.status === 1) {
        return {
          ...obj,
          statusText: "Done",
          statusProgress: 69.115,
        };
      } else if (obj.status === 2) {
        return {
          ...obj,
          statusText: "In process",
          statusProgress: 34.5575,
        };
      } else if (obj.status === 3) {
        return {
          ...obj,
          statusText: "To do",
          statusProgress: 0,
        };
      }
    });
};

export const TaskContextProvider = ({ children }) => {
  const [dataTask, mutateDataTask] = useState(addStructure(DEFAULT_TASKS));
  const [modalEditView, checkModalEditView] = useState({
    status: false,
    dataTaskElement: {},
  });

  const changeStatus = (id) => {
    DEFAULT_TASKS.map((obj) => {
      if (obj.id === id) {
        if (obj.status <= 2) {
          return (obj.status += 1);
        } else {
          return (obj.status = 1);
        }
      }
    });
    mutateDataTask(addStructure(DEFAULT_TASKS));
  };

  const requestEditModal = (id) => {
    const [taskElement] = dataTask.filter((obj) => obj.id === id);
    checkModalEditView({
      statusModal: true,
      ...taskElement,
    });
  };

  const editedTask = (task) => {
    DEFAULT_TASKS.map((obj) => {
      console.log(obj, "obj");
      if (obj.id === task.id) {
        console.log(obj, "objfilter");
        return {
          ...obj,
          name: task.name,
          priority: task.priority,
        };
      }
    });
    mutateDataTask(addStructure(DEFAULT_TASKS));
  };
  console.log()
  return (
    <TaskContext.Provider
      value={{
        data: dataTask,
        changeStatus: changeStatus,
        modalEditView: modalEditView,
        requestEditModal: requestEditModal,
        checkModalEditView: checkModalEditView,
        editedTask: editedTask,
      }}
    >
      {children}
    </TaskContext.Provider>
  );
};

export default TaskContext;
